import React, {Component} from 'react';
import { Text, View,ScrollView,FlatList,Image,AsyncStorage,TouchableOpacity} from 'react-native';
import PTRView from 'react-native-pull-to-refresh';
import { styles,config } from './Styles';


export default class Home extends Component<Props> {
constructor(props) {
    super(props);
    this.state = { 
     keylist:"00",
      myturns:[] ,
      percentage:40,
      inside:0,
      outSide:0

    };
  }
    _refresh() {
      this.FetchData()
    return new Promise((resolve) => {
      setTimeout(()=>{resolve()}, 2000)
    });


  }


async componentWillMount() 

  {


 const usertoken = await AsyncStorage.getItem('@MySuperStore:tokenuser');
   if (!usertoken) 
   {
    this.props.navigation.navigate('Register');
   }


  }

  

async FetchData ()
  {


 const tokenuser = await AsyncStorage.getItem('@MySuperStore:tokenuser');

      const response= await  fetch(config.Urlfetch+"currentstate", {
          method: 'POST',
          headers: {
            'Accept': 'application/json',
            'api-token': config.apiToken,
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            "admission": tokenuser,

          }),
        });

      if (response.status==200) {
 
       const json = await response.json();
      

       if (json.status==200) {

           this.setState({inside:json.inside,outSide:json.outside}) ; 
            

            
            }
           

          }
          else
          {
          this.setState({error:"Error From Server"})
          

          }

  }
 componentDidMount = () => {
 

  this._sub = this.props.navigation.addListener(
    'didFocus',
    this._componentFocused
  );
}

componentWillUnmount() {
  this._sub.remove();
}

_componentFocused = () => {
 this.FetchData()
}
  render() {

    return (
      <View style={styles.container}>

      

      <PTRView onRefresh={(e)=>this._refresh(e)} >

        

             <View style={styles.header}> 
                 <Text style={styles.TextHeader}> Admission Scanner </Text> 
              </View>

              
                
                     <View style={styles.boxstyle}>

                    <View style={styles.row}>
                        <Image  style={styles.childimage} source={{ uri: "data:image/jpeg;base64," }} />
                        <Text  style={styles.textname}>Behzad Rohizadeh</Text>

                    </View>

                    <View style={styles.row}>
                        <Text  style={styles.Textnote}>Total Inside </Text>
                        <Text  style={styles.Textnote}>Total OutSide </Text>
                    </View>

                    <View style={styles.row}>
                        <Text  style={styles.TextnoteValue}> {this.state.inside} </Text>
                        <Text  style={styles.TextnoteValue}> {this.state.outSide} </Text>
                    </View>



                     </View>
              
                  
               

      



         
          </PTRView>


          <View style={styles.byscanner} >
             <TouchableOpacity onPress={() => this.props.navigation.navigate("Qrcode")} 
             style={styles.buttonticket}
             >
              <Text style={{ fontSize: 17,color: 'white'}}> BY Scanner</Text>
            </TouchableOpacity>
          </View> 

           <View style={styles.byticketcode} >
             <TouchableOpacity onPress={() => this.props.navigation.navigate("Ticket")} 
             style={styles.buttonticket}
             >
              <Text style={{ fontSize: 17,color: 'white'}}> BY Ticket Code</Text>
            </TouchableOpacity>
          </View> 


 

  </View>
    );
  }
}

